package 변수;

public class 변수선언확인문제 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int age = 9096; //정수변수
		double phone = 2.22; //실수변수, 22.0, 22(정수를 넣을 수 있음)
		char c = '구'; //한글자만 쓸 땐 작은따옴표
		boolean b = true; //false

		System.out.println("휴대폰 가운데 번호는" + age);
		System.out.println("생일은" + phone);
		System.out.println("성은" + c);
		System.out.println("부울렌값은" + b);

	}

}
